mapper.update:
update shopping_cart
set amount = #{model.amount},
    sku_id = #{model.sku.id}
where id = #{model.id}
  and owner_id = #{model.owner.id}

mapper.delete:
delete
from shopping_cart
where id = #{id}
  and owner_id = #{ownerId}

mapper.select:
select id, product_id as "product.mid", sku_id as "sku.id", amount
from shopping_cart
where owner_id = #{ownerId}

select id, product_id as "product.mid", sku_id as "sku.id", amount
from shopping_cart
where id = #{id}

select id, product_id as "product.mid", sku_id as "sku.id", amount
from shopping_cart
where id = #{id}
