mapper.select:
select id, product_id as "product.mid", indexes, title, price, stock, image
from sku
where product_id = #{productId}

select id, product_id as "product.mid", indexes, title, price, stock, image
from sku
where id = #{id}

mapper.update:
update sku
set stock = stock - #{amount}
where id = #{id}
  and stock - #{amount} >= 0
